Q1
SELECT school_nam, ST_Y(geom), ST_X(geom)
FROM public.publicschools 
WHERE ST_X(geom) = 413797.2016872928
AND ST_Y(geom) = 124604.24301644415;

Q2
SELECT school_nam
FROM public.publicschools 
WHERE ST_DWithin(geom, 'SRID=26985; POINT(413797.2016872928 124604.24301644415)', 1000);

Q3
SELECT gid, geodesc
FROM public.pgcensustract
WHERE ST_Intersects(geom, ST_GeomFromText('POINT (417108 129547)', 26985));

Q4
SELECT fename
FROM public.pgroads
WHERE ST_DWithin(geom, 'SRID=26985; POINT(406286 124178)', 5000);

Q5
SELECT school_nam, ST_X(geom), ST_Y(geom)
FROM public.publicschools 
WHERE school_nam = 'Baden'
AND city = 'Brandywine';

SELECT fename, fetype
FROM public.pgroads
WHERE ST_DWithin(geom, 'SRID=26985; POINT(419875.79606161953 110341.10564772133)', 1000);

Q6
SELECT cen.geodesc, COUNT(sch.gid) as sum
FROM public.publicschools as sch
INNER JOIN public.pgcensustract as cen
    ON sch.geodesc = cen.geodesc
GROUP BY cen.geodesc;

Q7
SELECT cen.geodesc, COUNT(sch.gid) as sum
FROM public.publicschools as sch
INNER JOIN public.pgcensustract as cen
    ON sch.geodesc = cen.geodesc
GROUP BY cen.geodesc
ORDER BY sum desc
LIMIT 5;

SELECT ST_AsText(geom)
FROM pgcensustract
WHERE geodesc = '8040.01';

